void _dire_estab(U16 dire, WM_HWIN hItem)
{
	static char buf[10];
	U8 a = dire % 90;
	U8 b = dire / 90;

	memset(buf, 0, 10);
	if (a == 0) {
		switch (b)
		{
		case 0:
			TEXT_SetText(hItem, "south");
			break;
		case 1:
			TEXT_SetText(hItem, "west");
			break;
		case 2:
			TEXT_SetText(hItem, "north");
			break;
		case 3:
			TEXT_SetText(hItem, "east");
			break;
		default:
			break;
		}
	}
	else {
		switch (b)
		{
		case 0:
			sprintf(buf, "S->W %d", a);
			break;
		case 1:
			sprintf(buf, "W->N %d", a);
			break;
		case 2:
			sprintf(buf, "N->E %d", a);
			break;
		case 3:
			sprintf(buf, "E->S %d", a);
			break;
		default:
			break;
		}

		TEXT_SetText(hItem, buf);
	}

	

}
