//更新风速值
void gui_speed_update(U16 speed)
{
	g_gui_dev.speed = speed;

	__g_pMsg.MsgId = WM_PAINT;
	WM_SendMessage(g_gui_dev.hMenuWin, &__g_pMsg);
}

//更新风向值
void gui_dire_update(U16 dire)
{
	g_gui_dev.dire = dire;

	__g_pMsg.MsgId = WM_PAINT;
	WM_SendMessage(g_gui_dev.hMenuWin, &__g_pMsg);
}

